changeSet.sql:

CREATE OR REPLACE FUNCTION raise_audit_log_exception()
RETURNS trigger AS $$
BEGIN
  RAISE EXCEPTION 'audit_log is append-only';
END;
$$ LANGUAGE plpgsql;


DROP TRIGGER IF EXISTS prevent_update_audit_log ON audit_log;
CREATE TRIGGER prevent_update_audit_log
BEFORE UPDATE OR DELETE ON audit_log
FOR EACH ROW EXECUTE FUNCTION raise_audit_log_exception();
